hist_all = ['00','01','02','03','04','05','06','07','08','09','10']
#hist_all = ['00']
label_dir = "list/com30/histlabel/"
for i in hist_all:
    listdir="list/com30/hist"+i+"/"
    out1=open("list/com30/hist"+i+"_train.txt", "w")
    for num in train_com30:
        ct = open(listdir+"ctimg00{}".format(num), "r")
        label=open(label_dir+"ctimg00{}".format(num), "r")
        ctlines=ct.readlines()
        labellines=label.readlines()
        for ctline,labelline in zip(ctlines,labellines):
            ctline = ctline.replace('\\','/')
            labelline = labelline.replace('\\','/')
            out1.write(ctline.strip()+" "+labelline)
        ct.close()
        label.close()
    out1.close()
    out2=open("list/com30/hist"+i+"_val.txt", "w")
    for num in test_com30:
        ct = open(listdir+"ctimg00{}".format(num), "r")
        label=open(label_dir+"ctimg00{}".format(num), "r")
        ctlines=ct.readlines()
        labellines=label.readlines()
        for ctline,labelline in zip(ctlines,labellines):
            ctline = ctline.replace('\\','/')
            labelline = labelline.replace('\\','/')
            out2.write(ctline.strip()+" "+labelline)
        ct.close()
        label.close()
    out2.close()


